import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next';

const Orders = () => {
  const { t } = useTranslation();
  const [orders, setOrders] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [clients, setClients] = useState([]);
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    client_id: '',
    product_id: '',
    quantity: '',
    total_price: '',
    status: 'pending'
  });

  useEffect(() => {
    fetchOrders();
    fetchClients();
    fetchProducts();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/orders');
      setOrders(response.data);
    } catch (error) {
      console.error('Erro ao buscar pedidos:', error);
    }
  };

  const fetchClients = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/clients');
      setClients(response.data);
    } catch (error) {
      console.error('Erro ao buscar clientes:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Erro ao buscar produtos:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/orders', {
        ...formData,
        quantity: parseInt(formData.quantity),
        total_price: parseFloat(formData.total_price)
      });
      fetchOrders();
      setShowForm(false);
      setFormData({ client_id: '', product_id: '', quantity: '', total_price: '', status: 'pending' });
    } catch (error) {
      console.error('Erro ao criar pedido:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'delivered': return 'bg-success';
      case 'cancelled': return 'bg-danger';
      case 'shipped': return 'bg-info';
      case 'processing': return 'bg-primary';
      default: return 'bg-warning';
    }
  };

  const getTranslatedStatus = (status) => {
    const statusMap = {
      'pending': t('orders.statusOptions.pending'),
      'processing': t('orders.statusOptions.processing'),
      'shipped': t('orders.statusOptions.shipped'),
      'delivered': t('orders.statusOptions.delivered'),
      'cancelled': t('orders.statusOptions.cancelled')
    };
    return statusMap[status] || status;
  };

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>{t('orders.title')}</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? t('common.cancel') : t('orders.newOrder')}
        </button>
      </div>

      {showForm && (
        <div className="card mb-4">
          <div className="card-header">
            <h5 className="card-title mb-0">{t('orders.addOrder')}</h5>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6">
                  <div className="mb-3">
                    <label className="form-label">{t('orders.client')}</label>
                    <select
                      className="form-control"
                      name="client_id"
                      value={formData.client_id}
                      onChange={handleChange}
                      required
                    >
                      <option value="">{t('orders.selectClient')}</option>
                      {clients.map(client => (
                        <option key={client.id} value={client.id}>
                          {client.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="mb-3">
                    <label className="form-label">{t('orders.product')}</label>
                    <select
                      className="form-control"
                      name="product_id"
                      value={formData.product_id}
                      onChange={handleChange}
                      required
                    >
                      <option value="">{t('orders.selectProduct')}</option>
                      {products.map(product => (
                        <option key={product.id} value={product.id}>
                          {product.name} - R$ {product.price}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              </div>
              
              <div className="row">
                <div className="col-md-6">
                  <div className="mb-3">
                    <label className="form-label">{t('orders.quantity')}</label>
                    <input
                      type="number"
                      className="form-control"
                      name="quantity"
                      value={formData.quantity}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="mb-3">
                    <label className="form-label">{t('orders.totalPrice')}</label>
                    <input
                      type="number"
                      step="0.01"
                      className="form-control"
                      name="total_price"
                      value={formData.total_price}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>

              <div className="mb-3">
                <label className="form-label">{t('orders.status')}</label>
                <select
                  className="form-control"
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                >
                  <option value="pending">{t('orders.statusOptions.pending')}</option>
                  <option value="processing">{t('orders.statusOptions.processing')}</option>
                  <option value="shipped">{t('orders.statusOptions.shipped')}</option>
                  <option value="delivered">{t('orders.statusOptions.delivered')}</option>
                  <option value="cancelled">{t('orders.statusOptions.cancelled')}</option>
                </select>
              </div>
              
              <button type="submit" className="btn btn-success">
                {t('orders.saveOrder')}
              </button>
            </form>
          </div>
        </div>
      )}

      <div className="card">
        <div className="card-header">
          <h5 className="card-title mb-0">{t('orders.orderList')}</h5>
        </div>
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>{t('orders.clientHeader')}</th>
                  <th>{t('orders.productHeader')}</th>
                  <th>{t('orders.quantityHeader')}</th>
                  <th>{t('orders.totalHeader')}</th>
                  <th>{t('orders.statusHeader')}</th>
                  <th>{t('orders.dateHeader')}</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(order => (
                  <tr key={order.id}>
                    <td>{order.client_name}</td>
                    <td>{order.product_name}</td>
                    <td>{order.quantity}</td>
                    <td>R$ {parseFloat(order.total_price).toFixed(2)}</td>
                    <td>
                      <span className={`badge ${getStatusBadgeClass(order.status)}`}>
                        {getTranslatedStatus(order.status)}
                      </span>
                    </td>
                    <td>{new Date(order.order_date).toLocaleDateString('pt-BR')}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Orders;
