import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next';

const Orders = () => {
  const { t, i18n } = useTranslation();
  
  // Debug: verifique se as traduções estão carregando
  useEffect(() => {
    console.log('Idioma atual:', i18n.language);
    console.log('Tradução orders.title:', t('orders.title'));
    console.log('Todas as traduções disponíveis:', i18n.getResourceBundle('pt', 'translation'));
  }, [i18n.language, t]);

  const [orders, setOrders] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [clients, setClients] = useState([]);
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    client_id: '',
    product_id: '',
    quantity: '',
    total_price: '',
    status: 'pending'
  });

  useEffect(() => {
    fetchOrders();
    fetchClients();
    fetchProducts();
  }, []);

  // ... resto do código do Orders.js permanece o mesmo

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>{t('orders.title', 'Gerenciar Pedidos (fallback)')}</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? t('common.cancel', 'Cancelar') : t('orders.newOrder', 'Novo Pedido')}
        </button>
      </div>

      {/* ... resto do JSX usando t() para todas as strings */}
    </div>
  );
};

export default Orders;
